class Solution:
    def increasingTriplet(self, nums: 'List[int]') -> bool:
        # Because length of result is always less than 3, time complex of this algorithm is O(3 len(nums) ) (equals to O(n))
        result = []
        for num in nums:            
            index = len(result) - 1
            while index >= 0 and result[index] >= num:
                index -= 1
            if index == len(result) - 1:
                result.append(num)
                if len(result) == 3:
                    return True
            else:
                result[index + 1] = num
        return False


